import React, { useEffect, useRef } from "react";
import {gsap} from "gsap";
import ProjectCover from "./ProjectCover";
import ProjectInfo from "./ProjectInfo";

function Project({ project }) {
  const projectCoverRef = useRef(null);
  const projectInfoRef = useRef(null);

  useEffect(() => {
    const projectCover = projectCoverRef.current;
    const projectInfo = projectInfoRef.current;

    // Define your GSAP animation timeline
    const tl = gsap.timeline();
    tl.from(projectCover, { duration: 0.5, opacity: 0, y: 50 })
      .from(projectInfo, { duration: 0.5, opacity: 0, y: 50 }, "-=0.3");

    // Optionally, pause the animation initially
    tl.pause();

    // Function to play the animation when needed
    const playAnimation = () => {
      tl.play();
    };

    // Attach an event listener or use any user interaction
    // to trigger the animation, for example, a button click
    projectCover.addEventListener("click", playAnimation);

    // Cleanup: Remove the event listener and kill the animation when unmounting
    return () => {
      projectCover.removeEventListener("click", playAnimation);
      tl.kill();
    };
  }, []);

  return (
    <div className="projectsContainer">
      <div className="projectCover" ref={projectCoverRef}>
        <ProjectCover coverUrl={project.imageCoverUrl} />
      </div>
      <div className="projectInfo" ref={projectInfoRef}>
        <ProjectInfo
          image1={project.image1Url}
          description1={project.image1Desc}
          image2={project.image2Url}
          description2={project.image2Desc}
          image3={project.image3Url}
          description3={project.image3Desc}
        />
      </div>
    </div>
  );
}


export default Project;
