import React, { useRef, useEffect, useState } from "react";
import ProjectCover from "./ProjectCover";
import ProjectInfo from "./ProjectInfo";

function Project({ project }) {
  const projectCoverRef = useRef(null);
  const [showProjectInfo, setShowProjectInfo] = useState(false);

  // Function to handle the button click to show ProjectInfo
  const handleShowProjectInfo = () => {
    setShowProjectInfo(true);
  };

  useEffect(() => {
    const handleScroll = () => {
      const projectCover = projectCoverRef.current;
      if (projectCover) {
        const isScrolledToBottom =
          projectCover.scrollTop ===
          projectCover.scrollHeight - projectCover.clientHeight;
        if (isScrolledToBottom) {
          window.removeEventListener("scroll", handleScroll);
          // Show ProjectInfo when scrolling reaches the bottom
          setShowProjectInfo(true);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div className="projectsContainer">
      <div ref={projectCoverRef}>
        <ProjectCover
          coverUrl={project.imageCoverUrl}
          onShowProjectInfo={handleShowProjectInfo}
        />
      </div>
      {showProjectInfo && (
        <ProjectInfo
          image1={project.image1Url}
          description1={project.image1Desc}
          image2={project.image2Url}
          description2={project.image2Desc}
          image3={project.image3Url}
          description3={project.image3Desc}
        />
      )}
    </div>
  );
}

export default Project;
