import React, { useRef, useEffect } from "react";

function ProjectCover({ coverUrl, onScrollToBottom }) {
  const projectCoverRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const projectCover = projectCoverRef.current;
      if (projectCover) {
        const isScrolledToBottom =
          projectCover.scrollTop ===
          projectCover.scrollHeight - projectCover.clientHeight;
        if (isScrolledToBottom) {
          window.removeEventListener("scroll", handleScroll);
          // Call the provided callback function when scrolling reaches the bottom
          if (typeof onScrollToBottom === "function") {
            onScrollToBottom();
          }
        }
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [onScrollToBottom]);

  return (
    <div className="projectCoverContainer" ref={projectCoverRef}>
      <img src={coverUrl} alt="cover" />
      <button></button>
    </div>
  );
}

export default ProjectCover;
