import React, { useState, useEffect } from "react";
import ProjectCover from "./ProjectCover";
import ProjectInfo from "./ProjectInfo";

function Project({ project }) {
  const [isDetailVisible, setIsDetailVisible] = useState(false);

  // Function to toggle detail visibility
  function toggleDetailVisibility() {
    setIsDetailVisible(!isDetailVisible);
  }

  // Use useEffect to trigger animation when user scrolls down
  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY || window.pageYOffset;
      const projectElement = document.getElementById(`project-${project.id}`);
      
      if (projectElement && scrollY > projectElement.offsetTop - window.innerHeight / 2) {
        setIsDetailVisible(true);
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [project.id]);

  return (
    <div className="project" id={`project-${project.id}`}>
      <ProjectCover coverUrl={project.imageCoverUrl} />
      {isDetailVisible && (
        <div className="projectDetails">
          <ProjectInfo
            image1={project.image1Url}
            description1={project.image1Desc}
            image2={project.image2Url}
            description2={project.image2Desc}
            image3={project.image3Url}
            description3={project.image3Desc}
          />
        </div>
      )}
    </div>
  );
}

export default Project;
