import React, {useRef, useLayoutEffect} from "react";
import {gsap} from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import ProjectCover from "./ProjectCover";
import ProjectInfo from "./ProjectInfo";

gsap.registerPlugin(ScrollTrigger);

function Project({ project }) {

  const component = useRef();
  const slider = useRef();

  useLayoutEffect( () => {
    let ctx = gsap.context(() =>{
      let panels = gsap.utils.toArray(".panel");
      gsap.to(panels, {
        xPercent: -100 * (panels.length-1),
        ease: "none",
        scrollTrigger: {
          trigger: slider.current,
          pin: true,
          scrub: 1,
          snap: 1 / (panels.length - 1),
          end: () => "+=" + slider.current.offsetWidth
        }
      });
    }, component);
    return () => ctx.revert();
  });


  return (
    <div className="projectsContainer" ref={component}>
      <div ref={slider}>
      
      <div className="panel projectInfoContainer">
    
        <img src={project.image1Url} ></img>
        <p>{project.image1Desc}</p>
        <img src={project.image2Url} ></img>
        <p>{project.image2Desc}</p>
        <img src={project.image3Url} ></img>
        <p>{project.image3Desc}</p>
        </div>
        </div>
    </div>
  );
}

export default Project;
